int main(int argc, char **argv)
  { //Main routine start

    //Position to know where we are in the array
    int pos = 0;

    //Maximum characters allowed to be inputted + NULL
    char input[101];

    //Get's the user input
	while((userInput = getchar()) != '\n' && userInput != EOF)   {
        input[pos] = userInput;
	    pos++;  
	    printf("%c", userInput); 
	}

    //Counters
    input[pos] = '\0'; // Null
    printf("\n"); //puts blown up text on new line

    //Points the user's input to the correct place in the array, to then print out the input in the blown up, asterisk form.
    int i = 0 ;
    int j = 0 ;
    
    for (i = 0 ; i < 7; i++){
        for(j = 0 ; j < pos; j++){
            int offset = (65) + (input[j] - 97);
            printf("%s", characters[i][offset]);
        }
        printf("\n");
    }

    //Return code
    return 0;
  } //End main()